import { useState } from 'react'


function App() {

//recibe el Value y la función para cambiarlo. El useState es de react
//useState recibe al principio un string vacío ya que no existe texto al principio
//useState= hook de react 
const [text, setText] = useState('')

//useState recibe un array vacío que será el estado de la lista cuando está vacía.
const [todoList, setTodo] = useState([])

const [removedList, setRemove] = useState([])


// const handleClick = unit => setValue(value + unit)
const handleChange = event => setText(event.target.value)

const removeInputText = () =>{
  setText('')
}

const addTodo = () => {
  const newTodoList = todoList.concat(text)
  setTodo(newTodoList)
  removeInputText()
}

const removeTodoItem = event => setRemove(event.target.value)

const removeTodofn = () => {
  for (let i = 0; i< todoList.length; i++){
    if (todoList[i] === removedList){
      todoList.splice(1,i)
    }
  }
  setRemove(removeTodoList)
}


console.log('hola ', text)
console.log('boton ', todoList)
  return (
    <section>
      <div className="container">
    <input onChange={handleChange} value={text}/>
    <button onClick={addTodo}>ADD</button>
    <button onClick={removeInputText}>CLEAN</button>
      </div>
      <div>
        <ul>
          <div>{todoList.map((todo, index)=><li key={index}>{todo}</li> ) }<button onClick={removeTodofn}value={removeTodoItem}>DONE!</button></div>
        </ul>
      </div>

    </section>
  )
}


export default App
