  const newTodoList = todoList.concat(text)
  setTodo(newTodoList)
  removeInputText()
}

const removeTodo = () =>{
  const removeTodo = newTodoList.remove(todo)
  setTodo(removeTodo)
}


console.log('hola ', text)
console.log('boton ', todoList)
