    <button onClick={addTodo}>ADD</button>
    <button onClick={removeInputText}>CLEAN</button>
      </div>
      <div>
        <ul>
          <div>{todoList.map((todo, index)=><li key={index}>{todo}</li> ) }<button onClick={removeTodo}>DONE!</button></div>
        </ul>
      </div>

    </section>
  )
}

